import React, { useState, useEffect, useRef } from 'react';
import sensorService from '../services/sensorService';
import { 
  SENSOR_RANGES, 
  STATUS_COLORS, 
  getSensorStatus, 
  calculateIAQ, 
  getIAQStatus,
  WEATHER_CONFIG 
} from '../utils/constants';
import backgroundImage from '../assets/room-background.jpg';
import daikinLogo from '../assets/daikin-logo.png';

// Try to import building overview image
let buildingOverviewImage;
try {
  buildingOverviewImage = require('../assets/building-overview.png');
} catch (e) {
  try {
    buildingOverviewImage = require('../assets/building-overview.jpg');
  } catch (e) {
    buildingOverviewImage = null;
  }
}

// Custom hook for smooth value transitions - ONLY FOR VALUES, NOT DESIGN
const useSmoothTransition = (targetValue, duration = 1500) => {
  const [currentValue, setCurrentValue] = useState(targetValue || 0);
  const startValueRef = useRef(targetValue || 0);
  const startTimeRef = useRef(Date.now());
  const animationRef = useRef(null);

  useEffect(() => {
    if (targetValue === null || targetValue === undefined) return;
    if (Math.abs(targetValue - currentValue) < 0.1) return;

    startValueRef.current = currentValue;
    startTimeRef.current = Date.now();

    const animate = () => {
      const elapsed = Date.now() - startTimeRef.current;
      const progress = Math.min(elapsed / duration, 1);

      const easeOutCubic = 1 - Math.pow(1 - progress, 3);

      const newValue = startValueRef.current + 
        (targetValue - startValueRef.current) * easeOutCubic;

      setCurrentValue(newValue);

      if (progress < 1) {
        animationRef.current = requestAnimationFrame(animate);
      }
    };

    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
    }
    animationRef.current = requestAnimationFrame(animate);

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [targetValue, duration, currentValue]);

  return currentValue;
};

const Dashboard = () => {
  const [sensorData, setSensorData] = useState({
    PM25: null,
    CO2: null,
    TEMPERATURE: null,
    HUMIDITY: null,
    TVOC: null,
    DIFFERENTIAL_PRESSURE: null
  });
  const [lastUpdate, setLastUpdate] = useState(new Date());
  const [connectionStatus, setConnectionStatus] = useState({
    connected: false,
    simulation: true
  });
  const [error, setError] = useState(null);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [isDataLoading, setIsDataLoading] = useState(true);

  // Helper function to get display value with fallback
  const getDisplayValue = (value, fallback = 0) => {
    if (value === null || value === undefined || isNaN(value)) {
      return fallback;
    }
    return value;
  };

  // ONLY smooth transitions for values - keep all your original designs
  const smoothPM25 = useSmoothTransition(getDisplayValue(sensorData.PM25, 16.5), 1500);
