import React from 'react';

const IAQIndex = ({ index, level, size = 'large' }) => {
  const getArcPath = (percentage) => {
    const angle = (percentage / 100) * 270 - 135; // 270 degrees arc starting from -135°
    const radians = (angle * Math.PI) / 180;
    const radius = size === 'large' ? 80 : 60;
    const centerX = size === 'large' ? 100 : 75;
    const centerY = size === 'large' ? 100 : 75;
    
    const x = centerX + radius * Math.cos(radians);
    const y = centerY + radius * Math.sin(radians);
    
    const largeArcFlag = percentage > 50 ? 1 : 0;
    
    return `M ${centerX - radius * Math.cos(2.356)} ${centerY - radius * Math.sin(2.356)}
            A ${radius} ${radius} 0 ${largeArcFlag} 1 ${x} ${y}`;
  };

  const percentage = Math.min(100, (index / 300) * 100);
  const svgSize = size === 'large' ? 200 : 150;

  return (
    <div className={`iaq-index ${size}`}>
      <div className="iaq-circle">
        <svg width={svgSize} height={svgSize} viewBox={`0 0 ${svgSize} ${svgSize}`}>
          {/* Background arc */}
          <path
            d={getArcPath(100)}
            fill="none"
            stroke="#374151"
            strokeWidth="8"
            strokeLinecap="round"
          />
          
          {/* Progress arc */}
          <path
            d={getArcPath(percentage)}
            fill="none"
            stroke={level.color}
            strokeWidth="8"
            strokeLinecap="round"
          />
        </svg>
        
        <div className="iaq-content">
          <div className="iaq-value">{index}</div>
          <div className="iaq-label">{level.label}</div>
          {size === 'large' && (
            <div className="iaq-emoji">
              {index <= 50 ? '😊' : 
               index <= 100 ? '🙂' : 
               index <= 150 ? '😐' : 
               index <= 200 ? '😟' : '😷'}
            </div>
          )}
        </div>
      </div>
      
      {size === 'large' && (
        <div className="iaq-description">
          {level.label} air quality
        </div>
      )}
    </div>
  );
};

export default IAQIndex;

