import React, { useState, useEffect } from 'react';
import SensorCard from './SensorCard';
import IAQIndex from './IAQIndex';
import WeatherWidget from './WeatherWidget';
import sensorService from '../services/sensorService';
import { SENSOR_RANGES } from '../utils/constants';

const Dashboard = () => {
  const [sensorData, setSensorData] = useState({});
  const [lastUpdate, setLastUpdate] = useState(null);
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    // Subscribe to sensor updates
    const unsubscribe = sensorService.subscribe((data) => {
      setSensorData(data);
      setLastUpdate(new Date(data.timestamp));
      setIsConnected(true);
    });

    // Start monitoring
    sensorService.startMonitoring(5000); // Update every 5 seconds

    // Cleanup on unmount
    return () => {
      unsubscribe();
      sensorService.stopMonitoring();
    };
  }, []);

  const formatLastUpdate = (date) => {
    if (!date) return '';
    return date.toLocaleString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  return (
    <div className="dashboard-container">
      {/* Header */}
      <header className="dashboard-header">
        <div className="header-content">
          <div className="logo-section">
            <h1 className="dashboard-title">Indoor Air Quality Monitor</h1>
            <div className="connection-status">
              <span className={`status-indicator ${isConnected ? 'connected' : 'disconnected'}`}>
                ‚óè
              </span>
              {isConnected ? 'Connected' : 'Disconnected'}
            </div>
          </div>
          
          <div className="header-info">
            {lastUpdate && (
              <div className="last-update">
                Last updated: {formatLastUpdate(lastUpdate)}
              </div>
            )}
          </div>
        </div>
      </header>

      {/* Main Dashboard Grid */}
      <main className="dashboard-grid">
        {/* IAQ Index - Main Feature */}
        <div className="grid-item iaq-main">
          <h2>Indoor Air Quality</h2>
          {sensorData.IAQ_INDEX !== undefined && sensorData.IAQ_LEVEL && (
            <IAQIndex 
              index={sensorData.IAQ_INDEX} 
              level={sensorData.IAQ_LEVEL}
              size="large"
            />
          )}
          <div className="iaq-status">Excellent air quality</div>
        </div>

        {/* Temperature */}
        <div className="grid-item sensor-item">
          <SensorCard
            title="Temperature"
            value={sensorData.TEMPERATURE || 0}
            unit="¬∞C"
            icon="üå°Ô∏è"
            sensorType="TEMPERATURE"
            showRange={true}
            range={SENSOR_RANGES.TEMPERATURE}
          />
          <div className="comfort-range">Comfortable Range</div>
        </div>

        {/* Humidity */}
        <div className="grid-item sensor-item">
          <SensorCard
            title="Humidity"
            value={sensorData.HUMIDITY || 0}
            unit="%"
            icon="üíß"
            sensorType="HUMIDITY"
            showRange={true}
            range={SENSOR_RANGES.HUMIDITY}
          />
          <div className="comfort-range">Comfortable Range</div>
        </div>

        {/* CO2 */}
        <div className="grid-item sensor-item">
          <SensorCard
            title="Carbon Dioxide"
            value={sensorData.CO2 || 0}
            unit="ppm"
            icon="ü´Å"
            sensorType="CO2"
            showRange={true}
            range={SENSOR_RANGES.CO2}
          />
          <div className="co2-status">CO‚ÇÇ Concentration</div>
        </div>

        {/* PM2.5 */}
        <div className="grid-item sensor-item">
          <SensorCard
            title="Particulate Matter"
            value={sensorData.PM25 || 0}
            unit="¬µg/m¬≥"
            icon="üå´Ô∏è"
            sensorType="PM25"
            showRange={true}
            range={SENSOR_RANGES.PM25}
          />
          <div className="pm25-status">PM2.5</div>
        </div>

        {/* Weather Widget */}
        <div className="grid-item weather-item">
          <WeatherWidget
            location={sensorData.location || "Bangkok, Thailand"}
            temperature={31.39}
            humidity={64.31}
            condition="Sunny"
          />
        </div>

        {/* TVOC */}
        <div className="grid-item sensor-item">
          <SensorCard
            title="Volatile Compounds"
            value={sensorData.TVOC || 0}
            unit="ppb"
            icon="üß™"
            sensorType="TVOC"
            showRange={true}
            range={SENSOR_RANGES.TVOC}
          />
          <div className="tvoc-status">TVOC</div>
        </div>

        {/* Differential Pressure */}
        <div className="grid-item sensor-item differential-pressure">
          <div className="pressure-gauge">
            <div className="gauge-container">
              <svg viewBox="0 0 200 120" className="gauge-svg">
                {/* Gauge background arc */}
                <path
                  d="M 20 100 A 80 80 0 0 1 180 100"
                  fill="none"
                  stroke="#374151"
                  strokeWidth="8"
                />
                
                {/* Gauge progress arc */}
                <path
                  d={`M 20 100 A 80 80 0 0 1 ${20 + (160 * (sensorData.DIFFERENTIAL_PRESSURE || 0) / 10)} ${100 - Math.sin(Math.PI * (sensorData.DIFFERENTIAL_PRESSURE || 0) / 10) * 80}`}
                  fill="none"
                  stroke="#10B981"
                  strokeWidth="8"
                  strokeLinecap="round"
                />
                
                {/* Gauge needle */}
                <line
                  x1="100"
                  y1="100"
                  x2={100 + 60 * Math.cos(Math.PI - Math.PI * (sensorData.DIFFERENTIAL_PRESSURE || 0) / 10)}
                  y2={100 - 60 * Math.sin(Math.PI - Math.PI * (sensorData.DIFFERENTIAL_PRESSURE || 0) / 10)}
                  stroke="#EF4444"
                  strokeWidth="3"
                  strokeLinecap="round"
                />
                
                {/* Center dot */}
                <circle cx="100" cy="100" r="4" fill="#EF4444" />
                
                {/* Scale markers */}
                <text x="30" y="110" fontSize="10" fill="#9CA3AF">0</text>
                <text x="90" y="40" fontSize="10" fill="#9CA3AF">5</text>
                <text x="170" y="110" fontSize="10" fill="#9CA3AF">10</text>
              </svg>
            </div>
            
            <div className="pressure-value">
              {(sensorData.DIFFERENTIAL_PRESSURE || 0).toFixed(2)}
              <span className="pressure-unit">Pa</span>
            </div>
            
            <div className="pressure-label">Differential Pressure</div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
